    private void Button_oneMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_Button_oneMouseClicked
        // TODO add your handling code here:
        System.out.println("This is button 1");
        String [] adj ={"1","3"};
          //Button_one.setText(" ");
        for(int i = 0; i < adj.length;i++){
            if ((jigsaw.arr[Integer.valueOf(adj[i])])== " "){
                //swap method;
                returningButton((Integer.valueOf(adj[i])),jigsaw.arr[0]);////////
                Button_one.setText(" ");
                jigsaw.arr[Integer.valueOf(adj[i])] = jigsaw.arr[0];
                
                jigsaw.arr[0] = " ";
                
                //Button_one.setText(jigsaw.arr[Integer.valueOf(adj[i])]);
                break;
            }
        }
        for (int i=1;i<=8;i++){
        	if(jigsaw.arr[i]==String.valueOf(i)){
        		count++;
        	}
        }
        if (count == 8){
        	JOptionPane.showMessageDialog(null,"BOOM\nYou Got It!!!");
        }
    }//GEN-LAST:event_Button_oneMouseClicked

