    private void returningButton(int i,String m){
    	switch(i){
    		case 0: Button_one.setText(m);
    		break;
    		case 1: Button_two.setText(m);
    		break;
    		case 2: Button_three.setText(m);
    		break;
    		case 3: Button_four.setText(m);
    		break;
    		case 4: Button_five.setText(m);
    		break;
    		case 5: Button_six.setText(m);
    		break;
    		case 6: Button_seven.setText(m);
    		break;
    		case 7: Button_eight.setText(m);
    		break;
    		case 8: Button_nine.setText(m);
        	}
    }/////////////////////
    
